from dash import html
import dash_bootstrap_components as dbc
from .content import content


def build_banner():
    return html.Div(
        id="banner",
        className="banner",
        children=[
            html.Div(
                id="banner-text",
                children=[
                    html.H5(content['title']),
                    html.H6(content['subtitle']),
                ],
            ),
            dbc.Card(html.Img(
                src="data:image/png;base64,"
            )),
        ])
